mapper.select:
select empno, ename, job, mgr, hiredate, sal, comm, deptno
from emp where 1=1

	and ename = #{ename}


	and job = #{job}


	and deptno = #{deptno}

select empno, ename, job, mgr, hiredate, sal, comm, deptno
from emp where ename in

	#{strEname}

mapper.update:
update emp
set empno = #{empno}

	, ename = #{ename}


	, job = #{job}


	, mgr = #{mgr}


	, hiredate = #{hiredate}


	, sal = #{sal}


	, comm = #{comm}


	, deptno = #{deptno}

where empno = #{empno}

mapper.select:
SELECT empno, ename, job, mgr, hiredate, sal, comm, deptno
FROM emp
WHERE ename = #{ename}
